#include <iostream>
#include <iomanip>
#include <cmath>
using namespace std;void Addition(double & a, double & b, double c, double d);void Subtraction(double & a, double & b, double c, double d);void Multiplcation(double & a, double & b, double c, double d);void Division(double & a, double & b, double c, double d);double Length(double a, double b);void Input(double & a, double & b);void Output(double a, double b);void Introduce();int main(){char choice;double a = 0;double b = 0;double c = 0;double d = 0;cout << "\nWelcome to the Complex Number Calculator!" << "\n";Introduce();while(choice != 'q'){Output(a, b);cout << "Enter what you want to do: ";cin >> choice;switch(choice){case '+':Input(c, d);Addition(a, b, c, d);break;case '-':Input(c, d);Subtraction(a, b, c, d);break;case '*':Input(c, d);Multiplcation(a, b, c, d);break;case '/':Input(c, d);Division(a, b, c, d);break;case 'l':cout << "The length of your number is " << Length(a, b) << "\n\n";break;case 'i':Input(a, b);break;case 'h':Introduce();break;case 'q':break; default:cout << "Not a valid choice!" << endl;break;}}cout << "\nThank you for using the half-broken calculator that I made!\n\n";return 0;}void Addition(double & a, double & b, double c, double d){a += c;b += d;}void Subtraction(double & a, double & b, double c, double d){a -= c;b -= d;}void Multiplcation(double & a, double & b, double c, double d){double a_temp = a;a = a * c - b * d;b = b * c + a_temp * d;}void Division(double & a, double & b, double c, double d){double a_temp = a;a = (a * c + b * d) / (c * c + d * d);b = (b * c - a_temp * d) / (c * c + d * d);}double Length(double a, double b){return sqrt((a * a) + (b * b));}void Input(double & a, double & b){cout << "\nPlease enter the real portion: ";cin >> a;cout << "Please enter the imaginary portion: ";cin >> b;cout << "\n";}void Output(double a, double b){cout << "Your current number is: \n";if(b > 0){cout << fixed << setprecision(2) << a << " + " << b << "i\n\n";}else if(b < 0){cout << fixed << setprecision(2) << a << " - " << abs(b) << "i\n\n";}else{cout << fixed << setprecision(2) << a << " + 0i\n\n";}}void Introduce(){cout << "\nHere are your options: \n";cout << "   + (addition)\n";cout << "   - (subtraction)\n";cout << "   * (multiplication)\n";cout << "   / (division)\n";cout << "   l (length)\n";cout << "   i (new input)\n";cout << "   q (quit)\n\n";cout << "Press 'h' to see these options again\n\n";}